// App.jsx
import React, { useState, useEffect, useRef } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useNavigate,
  useParams,
  useLocation,
} from "react-router-dom";
import { createClient } from "@supabase/supabase-js";
import "./App.css";

const supabaseUrl = import.meta.env.VITE_SUPABASE_URL;
const supabaseKey = import.meta.env.VITE_SUPABASE_ANON_KEY;
const supabase = createClient(supabaseUrl, supabaseKey);

function App() {
  const [userId, setUserId] = useState(null);
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  useEffect(() => {
    const storedUserId = localStorage.getItem("userId");
    if (storedUserId) {
      setUserId(storedUserId.toLowerCase());
      setIsLoggedIn(true);
    }
  }, []);

  const handleLogin = (username) => {
    const normalized = username.toLowerCase();
    setUserId(normalized);
    setIsLoggedIn(true);
    localStorage.setItem("userId", normalized);
  };

  const handleLogout = () => {
    setUserId(null);
    setIsLoggedIn(false);
    localStorage.removeItem("userId");
  };

  if (!isLoggedIn) {
    return <Login onLogin={handleLogin} />;
  }

  return (
    <Router>
      <div className="App">
        <header className="header">
          <Link to="/" className="logo">
            <span className="glitch" data-text="MUSICVERSE">
              MUSICVERSE
            </span>
          </Link>
          <div className="user-controls">
            <div className="user-badge">ðŸŽµ User: {userId}</div>
            <button onClick={handleLogout} className="btn btn-logout">
              LOGOUT
            </button>
          </div>
        </header>
        <Routes>
          <Route path="/" element={<Home userId={userId} />} />
          <Route path="/create" element={<CreatePost userId={userId} />} />
          <Route path="/post/:id" element={<PostPage userId={userId} />} />
          <Route path="/edit/:id" element={<EditPost userId={userId} />} />
        </Routes>
      </div>
    </Router>
  );
}

function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!username.trim() || !password.trim()) {
      setError("Please enter both username and password");
      return;
    }
    if (password.length < 4) {
      setError("Password must be at least 4 characters");
      return;
    }
    // normalize username to lowercase so user ids are case-insensitive
    const normalized = username.toLowerCase();
    onLogin(normalized);
  };

  return (
    <div className="login-container">
      <div className="login-box">
        <h1 className="login-title">
          <span className="glitch" data-text="MUSICVERSE">
            MUSICVERSE
          </span>
        </h1>
        <h2 className="login-subtitle">LOGIN TO CONTINUE</h2>
        <form onSubmit={handleSubmit} className="login-form">
          <input
            type="text"
            placeholder="Username"
            value={username}
            onChange={(e) => {
              setUsername(e.target.value);
              setError("");
            }}
            className="input-retro"
            autoComplete="username"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => {
              setPassword(e.target.value);
              setError("");
            }}
            className="input-retro"
            autoComplete="current-password"
          />
          {error && <div className="error-message">{error}</div>}
          <button type="submit" className="btn btn-primary btn-login">
            LOGIN
          </button>
        </form>
        <p className="login-note">
          Note: This is a simple session-based login. Just enter any username
          and password (4+ characters) to continue.
        </p>
      </div>
      <div className="login-intro">
        <h2>Share Your Musical Journey</h2>
        <p>
          ðŸŽµ Discover, create, and share music posts with a vibrant community of
          music lovers. From classical symphonies to modern beats, MusicVerse is
          your platform to celebrate the universal language of music.
        </p>
      </div>
    </div>
  );
}

function Home({ userId }) {
  const [posts, setPosts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState("created_at");
  const [searchTerm, setSearchTerm] = useState("");
  const [filterFlag, setFilterFlag] = useState("all");
  const [showFullContent, setShowFullContent] = useState(false);
  const [showSettings, setShowSettings] = useState(false);
  const [showGenreBadge, setShowGenreBadge] = useState(false);
  const [colorScheme, setColorScheme] = useState(() => {
    return localStorage.getItem("colorScheme") || "purple";
  });
  const navigate = useNavigate();

  useEffect(() => {
    fetchPosts();
  }, [sortBy, filterFlag]);

  // Seed demo data in an idempotent way: update existing posts (title match, case-insensitive)
  const seedInitialData = async () => {
    try {
      console.log("Ensuring demo posts exist (idempotent seed)...");

      const demoPosts = [
        {
          title: "J. Cole â€” 2014 Forest Hills Drive",
          content: "A timeless lyrical masterpiece. Best album of all time.",
          image_url:
            "data:image/jpeg;base64,",
          video_url:
            "https://www.youtube.com/watch?v=EyGf4LVdRmo&list=PLVtit4eDhYIKqEEA91JPfu2VYKKnmw7y9&index=1",
          flag: "Lyrical",
          user_id: "Natoli",
          upvotes: 10,
        },
        {
          title: "The Weeknd â€” After Hours",
          content: "Dark, moody, and cinematic â€” a favorite for late nights.",
          image_url:
            "data:image/jpeg;base64,",
          video_url: "https://www.youtube.com/watch?v=ygTZZpVkmKg",
          flag: "Pop",
          user_id: "Olani",
          upvotes: 8,
        },
        {
          title: "Michael Jackson â€” Thriller",
          content: "Iconic production and unmatched pop energy.",
          image_url:
            "data:image/jpeg;base64,",
          video_url: "https://www.youtube.com/watch?v=sOnqjkJTMaA",
          flag: "Pop",
          user_id: "Jaleta",
          upvotes: 9,
        },
        {
          title: "Lauryn Hill â€” The Miseducation of Lauryn Hill",
          content: "Soulful, raw and personal. A generational classic.",
          image_url:
            "data:image/jpeg;base64,",
          video_url:
            "https://www.youtube.com/watch?v=1sQjh261rU8&list=PL6xN0YYY4l0ZB_if93ZeDGou8tl3nuVCe",
          flag: "Lyrical",
          user_id: "Ebba",
          upvotes: 11,
        },
        {
          title: "Luke Faulkner â€” New Beginning",
          content:
            "A beautiful, lesser-known instrumental piece â€” a hidden gem.",
          image_url:
            "data:image/jpeg;base64,",
          video_url: "https://www.youtube.com/watch?v=6pw3Uo4htB4",
          flag: "Instrumental",
          user_id: "Meklit",
          upvotes: 7,
        },
      ];

      for (const demo of demoPosts) {
        // normalize user id for storage
        const normalizedUser = demo.user_id.toLowerCase();

        // try to find existing posts by title (case-insensitive)
        const { data: found, error: findErr } = await supabase
          .from("posts")
          .select("*")
          .ilike("title", demo.title);

        if (findErr) {
          console.error("Error searching for demo post:", findErr);
          continue;
        }

        let postId = null;

        if (found && found.length > 0) {
          // If multiple duplicates exist (from earlier runs), remove extras and keep one.
          postId = found[0].id;

          if (found.length > 1) {
            const duplicateIds = found.slice(1).map((p) => p.id);
            try {
              // remove comments attached to duplicate posts first to avoid orphaned rows
              await supabase
                .from("comments")
                .delete()
                .in("post_id", duplicateIds);
            } catch (delCmtErr) {
              console.error("Error deleting duplicate comments:", delCmtErr);
            }

            try {
              await supabase.from("posts").delete().in("id", duplicateIds);
              console.log("Removed duplicate demo posts:", duplicateIds);
            } catch (delErr) {
              console.error("Error deleting duplicate demo posts:", delErr);
            }
          }

          // update the canonical post with latest placeholder links and upvotes
          const { error: updateErr } = await supabase
            .from("posts")
            .update({
              content: demo.content,
              image_url: demo.image_url,
              video_url: demo.video_url,
              flag: demo.flag || null,
              user_id: normalizedUser,
              upvotes: demo.upvotes,
            })
            .eq("id", postId);

          if (updateErr) console.error("Error updating demo post:", updateErr);
        } else {
          const { data: inserted, error: insertErr } = await supabase
            .from("posts")
            .insert([
              {
                title: demo.title,
                content: demo.content,
                image_url: demo.image_url,
                video_url: demo.video_url,
                flag: demo.flag || null,
                user_id: normalizedUser,
                upvotes: demo.upvotes,
              },
            ])
            .select();

          if (insertErr) {
            console.error("Error inserting demo post:", insertErr);
            continue;
          }

          postId = inserted[0].id;
        }

        // Now ensure a couple of sample comments exist for this post (avoid duplicates)
        const commentsToEnsure = [];
        commentsToEnsure.push({
          post_id: postId,
          content: "Best album of all time.",
          user_id: normalizedUser === "natoli" ? "olani" : "natoli",
        });

        if (normalizedUser === "natoli") {
          commentsToEnsure.push({
            post_id: postId,
            content: "I disagree â€” it's overrated for me.",
            user_id: "jaleta",
          });
        } else if (normalizedUser === "mekliet") {
          commentsToEnsure.push({
            post_id: postId,
            content: "Hidden gem! Glad someone posted this.",
            user_id: "ebba",
          });
        } else {
          commentsToEnsure.push({
            post_id: postId,
            content: "Totally agree â€” this record shaped my taste.",
            user_id: "natoli",
          });
        }

        for (const c of commentsToEnsure) {
          // check if a similar comment already exists (by content & user, case-insensitive)
          const { data: existingComments, error: cFindErr } = await supabase
            .from("comments")
            .select("id")
            .eq("post_id", postId)
            .ilike("content", c.content)
            .ilike("user_id", c.user_id)
            .limit(1);

          if (cFindErr) {
            console.error("Error checking existing comments:", cFindErr);
            continue;
          }

          if (!existingComments || existingComments.length === 0) {
            const { error: cInsertErr } = await supabase
              .from("comments")
              .insert([
                {
                  post_id: postId,
                  content: c.content,
                  user_id: c.user_id,
                },
              ]);

            if (cInsertErr)
              console.error("Error inserting demo comment:", cInsertErr);
          }
        }
      }

      // refresh local list so seeded posts appear immediately
      await fetchPosts();
      console.log("Demo idempotent seed finished.");
    } catch (err) {
      console.error("Unexpected error during seed:", err);
    }
  };

  // seedInitialData removed â€” posts are seeded in Supabase

  useEffect(() => {
    document.documentElement.setAttribute("data-theme", colorScheme);
    localStorage.setItem("colorScheme", colorScheme);
  }, [colorScheme]);

  const fetchPosts = async () => {
    setLoading(true);
    let query = supabase.from("posts").select("*");

    if (filterFlag !== "all") {
      query = query.eq("flag", filterFlag);
    }

    query = query.order(sortBy, { ascending: false });

    const { data, error } = await query;
    if (error) console.error("Error fetching posts:", error);
    else setPosts(data || []);
    setLoading(false);
  };

  // Inline upvote on home feed (optimistic)
  const handleHomeUpvote = async (postId) => {
    const post = posts.find((p) => p.id === postId);
    if (!post) return;
    const newCount = (post.upvotes || 0) + 1;
    // optimistic update
    setPosts((prev) =>
      prev.map((p) => (p.id === postId ? { ...p, upvotes: newCount } : p))
    );
    const { error } = await supabase
      .from("posts")
      .update({ upvotes: newCount })
      .eq("id", postId);
    if (error) {
      // revert on error
      setPosts((prev) =>
        prev.map((p) => (p.id === postId ? { ...p, upvotes: post.upvotes } : p))
      );
      console.error("Failed to upvote:", error);
    }
  };

  const filteredPosts = posts.filter((post) =>
    post.title.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="container">
      <div className="controls-panel">
        <Link to="/create" className="btn btn-create pixel-corners">
          ðŸŽµ NEW POST
        </Link>

        <button
          onClick={() => setShowSettings(!showSettings)}
          className="btn btn-settings"
        >
          âš™ï¸ SETTINGS
        </button>

        <div className="control-group">
          <label>Sort by:</label>
          <select
            value={sortBy}
            onChange={(e) => setSortBy(e.target.value)}
            className="select-retro"
          >
            <option value="created_at">Time</option>
            <option value="upvotes">Upvotes</option>
          </select>
        </div>

        <div className="control-group">
          <label>Genre:</label>
          <select
            value={filterFlag}
            onChange={(e) => setFilterFlag(e.target.value)}
            className="select-retro"
          >
            <option value="all">All Genres</option>
            <option value="Classical">Classical</option>
            <option value="Lyrical">Lyrical</option>
            <option value="Instrumental">Instrumental</option>
            <option value="Ambient">Ambient</option>
            <option value="Jazz">Jazz</option>
            <option value="Pop">Pop</option>
            <option value="Study Music">Study Music</option>
          </select>
        </div>

        <input
          type="text"
          placeholder="Search posts..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="search-input"
        />
      </div>

      {showSettings && (
        <div className="settings-panel">
          <h3 className="settings-title">âš™ï¸ SETTINGS</h3>
          <div className="setting-item">
            <label>Color Scheme:</label>
            <select
              value={colorScheme}
              onChange={(e) => setColorScheme(e.target.value)}
              className="select-retro"
            >
              <option value="purple">Purple Haze</option>
              <option value="blue">Ocean Blue</option>
              <option value="pink">Neon Pink</option>
              <option value="green">Matrix Green</option>
              <option value="orange">Sunset Orange</option>
            </select>
          </div>
          <div className="setting-item">
            <label className="checkbox-label">
              <input
                type="checkbox"
                checked={showFullContent}
                onChange={(e) => {
                  const checked = e.target.checked;
                  setShowFullContent(checked);
                  setShowGenreBadge(checked);
                }}
              />
              Show full content on home feed
            </label>
          </div>
          <div className="user-id-display">Your ID: {userId}</div>
        </div>
      )}

      {loading ? (
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <div className="loading-text">LOADING...</div>
        </div>
      ) : (
        <div className="posts-grid">
          {filteredPosts.map((post) => (
            <div
              key={post.id}
              className={`post-card ${
                showFullContent && post.image_url ? "full-card" : ""
              }`}
              style={{ position: "relative" }}
              onClick={() => {
                if (!showFullContent) navigate(`/post/${post.id}`);
              }}
            >
              {showGenreBadge && post.flag && (
                <span
                  className="flag-badge"
                  style={{
                    position: "absolute",
                    top: "8px",
                    right: "8px",
                    zIndex: 1000,
                  }}
                >
                  {post.flag}
                </span>
              )}
              <div className="post-header">
                <h3 className="post-title">{post.title}</h3>
              </div>
              <div className="post-meta">
                <span className="time-badge">
                  {new Date(post.created_at).toLocaleString()}
                </span>
                <span className="upvote-badge">â–² {post.upvotes}</span>
              </div>

              {showFullContent && (
                <>
                  {post.image_url ? (
                    <img
                      src={post.image_url}
                      alt={post.title}
                      className="post-image-grid dimmed"
                      onClick={(e) => {
                        e.stopPropagation();
                        navigate(`/post/${post.id}?focus=comments`);
                      }}
                    />
                  ) : (
                    post.content && (
                      <p className="post-preview">
                        {post.content.substring(0, 150)}...
                      </p>
                    )
                  )}

                  <div className="grid-footer">
                    <div className="meta-block">
                      <div className="title-line">{post.title}</div>
                      <div className="meta-sub">
                        <span className="meta-time">
                          {new Date(post.created_at).toLocaleDateString()}
                        </span>
                        {post.user_id && (
                          <span className="meta-author">by {post.user_id}</span>
                        )}
                      </div>
                    </div>
                    <div className="icon-actions">
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          navigate(`/post/${post.id}?focus=comments`);
                        }}
                        className="icon-button comment"
                        title="Open comments"
                      >
                        ðŸ’¬
                      </button>
                      <button
                        onClick={(e) => {
                          e.stopPropagation();
                          handleHomeUpvote(post.id);
                        }}
                        className="icon-button upvote"
                        title="Upvote"
                      >
                        â–² {post.upvotes}
                      </button>
                    </div>
                  </div>
                </>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

function CreatePost({ userId }) {
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [flag, setFlag] = useState("");
  const [repostId, setRepostId] = useState("");
  const [imageFile, setImageFile] = useState(null);
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const handleImageUpload = async (file) => {
    const fileExt = file.name.split(".").pop();
    const owner = userId ? userId.toLowerCase() : "anon";
    const fileName = `${owner}_${Date.now()}.${fileExt}`;
    const { data, error } = await supabase.storage
      .from("post-images")
      .upload(fileName, file);

    if (error) {
      console.error("Error uploading image:", error);
      return null;
    }

    const { data: urlData } = supabase.storage
      .from("post-images")
      .getPublicUrl(fileName);

    return urlData.publicUrl;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    let finalImageUrl = imageUrl;
    if (imageFile) {
      finalImageUrl = await handleImageUpload(imageFile);
    }

    const { data, error } = await supabase
      .from("posts")
      .insert([
        {
          title,
          content,
          image_url: finalImageUrl,
          video_url: videoUrl,
          flag: flag || null,
          repost_id: repostId || null,
          user_id: userId && userId.toLowerCase(),
          upvotes: 0,
        },
      ])
      .select();

    setLoading(false);

    if (error) {
      console.error("Error creating post:", error);
      alert("Error creating post");
    } else {
      navigate(`/post/${data[0].id}`);
    }
  };

  return (
    <div className="container">
      <div className="form-container">
        <h2 className="form-title">CREATE NEW POST</h2>
        {loading ? (
          <div className="loading-container">
            <div className="loading-spinner"></div>
            <div className="loading-text">CREATING...</div>
          </div>
        ) : (
          <form onSubmit={handleSubmit} className="post-form">
            <input
              type="text"
              placeholder="Post Title *"
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
              className="input-retro"
            />
            <textarea
              placeholder="Content (optional)"
              value={content}
              onChange={(e) => setContent(e.target.value)}
              className="textarea-retro"
              rows="6"
            />
            <input
              type="text"
              placeholder="Image URL (optional)"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
              className="input-retro"
            />
            <div className="file-upload">
              <label className="file-label">
                Upload Image:
                <input
                  type="file"
                  accept="image/*"
                  onChange={(e) => setImageFile(e.target.files[0])}
                  className="file-input"
                />
              </label>
              {imageFile && <span className="file-name">{imageFile.name}</span>}
            </div>
            <input
              type="text"
              placeholder="Video URL (optional)"
              value={videoUrl}
              onChange={(e) => setVideoUrl(e.target.value)}
              className="input-retro"
            />
            <select
              value={flag}
              onChange={(e) => setFlag(e.target.value)}
              className="select-retro"
            >
              <option value="">No Genre</option>
              <option value="Classical">Classical</option>
              <option value="Lyrical">Lyrical</option>
              <option value="Instrumental">Instrumental</option>
              <option value="Ambient">Ambient</option>
              <option value="Jazz">Jazz</option>
              <option value="Pop">Pop</option>
              <option value="Study Music">Study Music</option>
            </select>
            <input
              type="text"
              placeholder="Repost ID (optional)"
              value={repostId}
              onChange={(e) => setRepostId(e.target.value)}
              className="input-retro"
            />
            <div className="button-group">
              <button type="submit" className="btn btn-primary">
                POST
              </button>
              <Link to="/" className="btn btn-secondary">
                CANCEL
              </Link>
            </div>
          </form>
        )}
      </div>
    </div>
  );
}

function PostPage({ userId }) {
  const { id } = useParams();
  const location = useLocation();
  const [post, setPost] = useState(null);
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [repostedPost, setRepostedPost] = useState(null);
  const [loading, setLoading] = useState(true);
  const [deleteKey, setDeleteKey] = useState("");
  const [showDeletePrompt, setShowDeletePrompt] = useState(false);
  const navigate = useNavigate();
  const commentsRef = useRef(null);

  useEffect(() => {
    fetchPost();
    fetchComments();
  }, [id]);

  const fetchPost = async () => {
    setLoading(true);
    const { data, error } = await supabase
      .from("posts")
      .select("*")
      .eq("id", id)
      .single();

    if (error) console.error("Error fetching post:", error);
    else {
      setPost(data);
      if (data.repost_id) {
        fetchRepostedPost(data.repost_id);
      }
    }
    setLoading(false);
  };

  const fetchRepostedPost = async (repostId) => {
    const { data, error } = await supabase
      .from("posts")
      .select("*")
      .eq("id", repostId)
      .single();

    if (!error) setRepostedPost(data);
  };

  const fetchComments = async () => {
    const { data, error } = await supabase
      .from("comments")
      .select("*")
      .eq("post_id", id)
      .order("created_at", { ascending: false });

    if (error) console.error("Error fetching comments:", error);
    else setComments(data || []);
  };

  // Scroll to comments when URL contains ?focus=comments
  useEffect(() => {
    if (
      location &&
      location.search &&
      location.search.includes("focus=comments")
    ) {
      // wait for comments to render
      setTimeout(() => {
        if (commentsRef.current) {
          commentsRef.current.scrollIntoView({
            behavior: "smooth",
            block: "start",
          });
        }
      }, 150);
    }
  }, [location.search, comments]);

  const handleUpvote = async () => {
    if (!post) return;
    const { error } = await supabase
      .from("posts")
      .update({ upvotes: post.upvotes + 1 })
      .eq("id", id);

    if (!error) {
      setPost({ ...post, upvotes: post.upvotes + 1 });
    }
  };

  const handleAddComment = async (e) => {
    e.preventDefault();
    if (!newComment.trim()) return;

    const { error } = await supabase.from("comments").insert([
      {
        post_id: id,
        content: newComment,
        user_id: userId && userId.toLowerCase(),
      },
    ]);

    if (!error) {
      setNewComment("");
      fetchComments();
    }
  };

  const handleDelete = async () => {
    const { error } = await supabase.from("posts").delete().eq("id", id);
    if (!error) {
      navigate("/");
    } else {
      alert("Error deleting post");
    }
  };

  const handleDeleteComment = async (commentId, commentUserId) => {
    const isCommentAuthor =
      (commentUserId || "").toLowerCase() === (userId || "").toLowerCase();
    const isPostOwner =
      post &&
      (post.user_id || "").toLowerCase() === (userId || "").toLowerCase();

    if (!isCommentAuthor && !isPostOwner) {
      alert("You can only delete your own comments or comments on your post");
      return;
    }
    const { data, error } = await supabase
      .from("comments")
      .delete()
      .eq("id", commentId)
      .select();

    if (error) {
      console.error("Error deleting comment:", error);
      alert("Error deleting comment");
    } else {
      // remove locally so it persists in the UI without a reload
      setComments((prev) => prev.filter((c) => c.id !== commentId));
    }
  };

  const canModify =
    post && (post.user_id || "").toLowerCase() === (userId || "").toLowerCase();

  if (loading) {
    return (
      <div className="container">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <div className="loading-text">LOADING...</div>
        </div>
      </div>
    );
  }

  if (!post) {
    return (
      <div className="container">
        <p>Post not found</p>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="post-detail">
        <div className="post-top-actions">
          <Link to="/" className="btn btn-back top">
            â† BACK TO FEED
          </Link>
        </div>
        <div className="post-detail-header">
          <h1 className="post-detail-title">{post.title}</h1>
          {post.flag && <span className="flag-badge large">{post.flag}</span>}
        </div>
        <div className="post-meta">
          <span className="time-badge">
            {new Date(post.created_at).toLocaleString()}
          </span>
          <span className="upvote-badge">â–² {post.upvotes}</span>
          {post.user_id && <span className="author">by {post.user_id}</span>}
        </div>

        {repostedPost && (
          <div className="repost-container">
            <div className="repost-label">REPOSTING:</div>
            <div
              className="repost-card"
              onClick={() => navigate(`/post/${repostedPost.id}`)}
            >
              <h4>{repostedPost.title}</h4>
              <p>{repostedPost.content?.substring(0, 100)}...</p>
            </div>
          </div>
        )}

        {post.content && <p className="post-content">{post.content}</p>}

        {post.image_url && (
          <img src={post.image_url} alt="Post" className="post-image" />
        )}

        {post.video_url && (
          <div className="video-container">
            <iframe
              src={post.video_url.replace("watch?v=", "embed/")}
              frameBorder="0"
              allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
              allowFullScreen
              className="post-video"
            ></iframe>
          </div>
        )}

        <div className="action-buttons">
          <button onClick={handleUpvote} className="btn btn-upvote">
            â–² UPVOTE
          </button>
          {canModify && (
            <>
              <Link to={`/edit/${post.id}`} className="btn btn-edit">
                EDIT
              </Link>
              <button
                onClick={() => setShowDeletePrompt(true)}
                className="btn btn-delete"
              >
                DELETE
              </button>
            </>
          )}
        </div>

        {showDeletePrompt && (
          <div className="delete-prompt">
            <p>Are you sure you want to delete this post?</p>
            <button onClick={handleDelete} className="btn btn-delete">
              CONFIRM DELETE
            </button>
            <button
              onClick={() => setShowDeletePrompt(false)}
              className="btn btn-secondary"
            >
              CANCEL
            </button>
          </div>
        )}

        <div className="comments-section" ref={commentsRef}>
          <h3 className="comments-title">COMMENTS</h3>
          <form onSubmit={handleAddComment} className="comment-form">
            <textarea
              value={newComment}
              onChange={(e) => setNewComment(e.target.value)}
              placeholder="Add a comment..."
              className="textarea-retro"
              rows="3"
            />
            <button type="submit" className="btn btn-primary">
              POST COMMENT
            </button>
          </form>

          <div className="comments-list">
            {comments.map((comment) => (
              <div key={comment.id} className="comment-card">
                <div className="comment-header">
                  <span className="comment-author">{comment.user_id}</span>
                  <span className="comment-time">
                    {new Date(comment.created_at).toLocaleString()}
                  </span>
                </div>
                <p className="comment-content">{comment.content}</p>
                {((comment.user_id || "").toLowerCase() ===
                  (userId || "").toLowerCase() ||
                  (post &&
                    (post.user_id || "").toLowerCase() ===
                      (userId || "").toLowerCase())) && (
                  <button
                    onClick={() =>
                      handleDeleteComment(comment.id, comment.user_id)
                    }
                    className="btn-delete-comment"
                  >
                    DELETE
                  </button>
                )}
              </div>
            ))}
          </div>
        </div>

        <Link to="/" className="btn btn-back">
          â† BACK TO FEED
        </Link>
      </div>
    </div>
  );
}

function EditPost({ userId }) {
  const { id } = useParams();
  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [videoUrl, setVideoUrl] = useState("");
  const [flag, setFlag] = useState("");
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    fetchPost();
  }, [id]);

  const fetchPost = async () => {
    const { data, error } = await supabase
      .from("posts")
      .select("*")
      .eq("id", id)
      .single();

    if (error) {
      console.error("Error fetching post:", error);
      navigate("/");
    } else {
      if ((data.user_id || "").toLowerCase() !== (userId || "").toLowerCase()) {
        alert("You can only edit your own posts");
        navigate("/");
        return;
      }
      setTitle(data.title);
      setContent(data.content || "");
      setImageUrl(data.image_url || "");
      setVideoUrl(data.video_url || "");
      setFlag(data.flag || "");
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);

    const { error } = await supabase
      .from("posts")
      .update({
        title,
        content,
        image_url: imageUrl,
        video_url: videoUrl,
        flag: flag || null,
      })
      .eq("id", id);

    setLoading(false);

    if (error) {
      console.error("Error updating post:", error);
      alert("Error updating post");
    } else {
      navigate(`/post/${id}`);
    }
  };

  if (loading) {
    return (
      <div className="container">
        <div className="loading-container">
          <div className="loading-spinner"></div>
          <div className="loading-text">LOADING...</div>
        </div>
      </div>
    );
  }

  return (
    <div className="container">
      <div className="form-container">
        <h2 className="form-title">EDIT POST</h2>
        <form onSubmit={handleSubmit} className="post-form">
          <input
            type="text"
            placeholder="Post Title *"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            required
            className="input-retro"
          />
          <textarea
            placeholder="Content (optional)"
            value={content}
            onChange={(e) => setContent(e.target.value)}
            className="textarea-retro"
            rows="6"
          />
          <input
            type="text"
            placeholder="Image URL (optional)"
            value={imageUrl}
            onChange={(e) => setImageUrl(e.target.value)}
            className="input-retro"
          />
          <input
            type="text"
            placeholder="Video URL (optional)"
            value={videoUrl}
            onChange={(e) => setVideoUrl(e.target.value)}
            className="input-retro"
          />
          <select
            value={flag}
            onChange={(e) => setFlag(e.target.value)}
            className="select-retro"
          >
            <option value="">No Genre</option>
            <option value="Classical">Classical</option>
            <option value="Lyrical">Lyrical</option>
            <option value="Instrumental">Instrumental</option>
            <option value="Ambient">Ambient</option>
            <option value="Jazz">Jazz</option>
            <option value="Pop">Pop</option>
            <option value="Study Music">Study Music</option>
          </select>
          <div className="button-group">
            <button type="submit" className="btn btn-primary">
              UPDATE
            </button>
            <Link to={`/post/${id}`} className="btn btn-secondary">
              CANCEL
            </Link>
          </div>
        </form>
      </div>
    </div>
  );
}

export default App;
